import React, { useEffect, useState } from "react";
import { Box, Typography, Grid, Paper, CircularProgress } from "@mui/material";
import { getAllOrders } from "../api/orders";

export default function AdminOrdersPage() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    getAllOrders()
      .then(res => setOrders(res.data))
      .catch(console.error)
      .finally(() => setLoading(false));
  }, []);

  if (loading)
    return <CircularProgress sx={{ mt: 5, display: "block", mx: "auto" }} />;

  if (!orders.length)
    return <Typography sx={{ p: 3 }}>No orders have been placed yet.</Typography>;

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" sx={{ mb: 3 }}>All Orders</Typography>
      <Grid container spacing={2}>
        {orders.map(order => (
          <Grid item xs={12} key={order.id}>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6">Order ID: {order.id}</Typography>
              <Typography>User: {order.userName}</Typography>
              <Typography>Total: ₹{order.totalPrice}</Typography>
              <Typography>
                Delivery Date: {new Date(order.deliveryDate).toLocaleDateString()}
              </Typography>
              <Typography>Delivery Slot: {order.deliverySlot}</Typography>

              <Box sx={{ mt: 1 }}>
                <Typography variant="subtitle1">Items:</Typography>
                {order.items.map(item => (
                  <Paper key={item.id} sx={{ p: 1, mt: 1 }}>
                    <Typography>Name: {item.product?.name}</Typography>
                    <Typography>Quantity: {item.quantity}</Typography>
                    <Typography>Price: ₹{item.finalPrice}</Typography>
                    <Typography>Options: {item.selectedOptions}</Typography>
                  </Paper>
                ))}
              </Box>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}
