import React, { useEffect, useState } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";
import PriceBox from "../components/Customizer/PriceBox";
import SingleOptionSelector from "../components/Customizer/SingleOptionSelector";
import ToppingsSelector from "../components/Customizer/ToppingsSelector";

const CustomizerPage = () => {
  const { productId } = useParams();
  const navigate = useNavigate();
  const [product, setProduct] = useState(null);
  const [options, setOptions] = useState({});
  const [selectedSingle, setSelectedSingle] = useState({});
  const [selectedToppings, setSelectedToppings] = useState([]);
  const [quantity, setQuantity] = useState(1);
  const [totalPrice, setTotalPrice] = useState(0);

  // Fetch product
  useEffect(() => {
    axios.get(`http://localhost:8080/api/products/${productId}`)
      .then(res => setProduct(res.data))
      .catch(err => console.log(err));
  }, [productId]);

  // Fetch options
  useEffect(() => {
    const types = ["flavor", "size", "topping", "shape", "decoration"];
    types.forEach(type => {
      axios.get(`http://localhost:8080/api/options/type/${type}`)
        .then(res => setOptions(prev => ({ ...prev, [type]: res.data })))
        .catch(err => console.log(err));
    });
  }, []);

  // Calculate price
  useEffect(() => {
    if (!product) return;

    const payload = {
      basePrice: product.basePrice,
      selectedSingle,
      selectedToppings,
      quantity
    };

    axios.post("http://localhost:8080/api/price/calculate", payload)
      .then(res => setTotalPrice(res.data.total))
      .catch(err => console.log(err));
  }, [product, selectedSingle, selectedToppings, quantity]);

  const handleAddToCart = () => {
    const payload = {
      productId: product.id,
      name: product.name,
      image: product.image,
      basePrice: product.basePrice,
      selectedSingle,
      selectedToppings,
      quantity,
      totalPrice
    };

    axios.post("http://localhost:8080/api/cart", payload)
      .then(() => {
        alert("Added to cart!");
        navigate("/cart");
      })
      .catch(err => console.log(err));
  };

  if (!product) return <p>Loading...</p>;

  return (
    <div style={{ padding: "40px" }}>
      <h2>{product.name}</h2>
      <img
        src={`/images/${product.image}`}
        alt={product.name}
        style={{ width: "300px", borderRadius: "10px" }}
      />

      <div style={{ marginTop: "30px" }}>
        {["flavor", "size", "shape", "decoration"].map(optType => (
          <SingleOptionSelector
            key={optType}
            title={optType}
            options={options[optType] || []}
            selected={selectedSingle[optType] || ""}
            onChange={value => setSelectedSingle(prev => ({ ...prev, [optType]: value }))}
          />
        ))}

        <ToppingsSelector
          options={options["topping"] || []}
          selected={selectedToppings}
          onChange={setSelectedToppings}
        />
      </div>

      <div style={{ marginTop: "20px" }}>
        <label>Quantity: </label>
        <input
          type="number"
          min={1}
          value={quantity}
          onChange={e => setQuantity(Math.max(1, parseInt(e.target.value) || 1))}
          style={{ width: "60px", marginLeft: "10px" }}
        />
      </div>

      <PriceBox price={totalPrice} />

      <button
        onClick={handleAddToCart}
        style={{
          marginTop: "20px",
          backgroundColor: "#ff4b81",
          color: "white",
          padding: "12px 25px",
          borderRadius: "8px",
          border: "none",
          cursor: "pointer",
        }}
      >
        Add to Cart
      </button>
    </div>
  );
};

export default CustomizerPage;
