import React, { useEffect, useState } from "react";
import { Box, Typography, Grid, Paper, Button, TextField } from "@mui/material";
import { getCart, removeFromCart, updateCartItem } from "../api/cart";
import { useNavigate } from "react-router-dom";

export default function CartPage() {
  const [items, setItems] = useState([]);
  const navigate = useNavigate();

  const fetch = () => {
    getCart().then(res => setItems(res.data)).catch(console.error);
  };

  useEffect(() => { fetch(); }, []);

  const handleRemove = (id) => {
    removeFromCart(id).then(fetch).catch(console.error);
  };

  const handleQty = (id, qty) => {
    if (qty < 1) return;
    updateCartItem(id, { quantity: qty }).then(fetch).catch(console.error);
  };

  const total = items.reduce((s, it) => s + (it.totalPrice ?? (it.basePrice * (it.quantity || 1))), 0);

  if (!items.length) return <Typography sx={{ p: 3 }}>Your cart is empty.</Typography>;

  return (
    <Box>
      <Typography variant="h4" sx={{ mb: 2 }}>My Cart</Typography>
      <Grid container spacing={2}>
        {items.map(it => (
          <Grid item xs={12} key={it.id}>
            <Paper sx={{ display: "flex", alignItems: "center", p: 2 }}>
              <img src={`/images/${it.image}`} alt={it.name} style={{ width: 120, borderRadius: 8, marginRight: 16 }} />
              <Box sx={{ flex: 1 }}>
                <Typography variant="h6">{it.name}</Typography>
                <Typography variant="body2">Options: {JSON.stringify(it.selectedSingle)}</Typography>
                <Typography variant="body2">Toppings: {(it.selectedToppings || []).join(", ")}</Typography>
              </Box>
              <Box sx={{ display: "flex", flexDirection: "column", alignItems: "flex-end" }}>
                <TextField
                  type="number"
                  value={it.quantity}
                  onChange={(e) => handleQty(it.id, Math.max(1, parseInt(e.target.value || "1")))}
                  sx={{ width: 90, mb: 1 }}
                  InputProps={{ inputProps: { min: 1 } }}
                />
                <Typography variant="subtitle1">₹{it.totalPrice ?? (it.basePrice * it.quantity)}</Typography>
                <Button color="error" onClick={() => handleRemove(it.id)} sx={{ mt: 1 }}>Remove</Button>
              </Box>
            </Paper>
          </Grid>
        ))}
      </Grid>

      <Typography variant="h5" sx={{ mt: 3 }}>Total: ₹{total}</Typography>
      <Button variant="contained" color="primary" sx={{ mt: 2 }} onClick={() => navigate("/checkout")}>Proceed to Checkout</Button>
    </Box>
  );
}
