import React, { useEffect, useState } from "react";
import { Box, Typography, TextField, Button } from "@mui/material";
import { getCart } from "../api/cart";
import { placeOrder } from "../api/orders";
import { useNavigate } from "react-router-dom";

export default function Checkout() {
  const [cart, setCart] = useState([]);
  const [name, setName] = useState("");
  const [address, setAddress] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    getCart().then(res => setCart(res.data)).catch(console.error);
  }, []);

  const total = cart.reduce((s, it) => s + (it.totalPrice ?? (it.basePrice * it.quantity)), 0);

  const submit = () => {
    if (!name || !address) return alert("Please fill name and address");
    placeOrder({ userName: name, totalPrice: total, items: cart })
      .then(() => {
        alert("Order placed");
        navigate("/");
      })
      .catch(console.error);
  };

  if (!cart.length) return <Typography sx={{ p: 3 }}>Your cart is empty.</Typography>;

  return (
    <Box>
      <Typography variant="h4" sx={{ mb: 2 }}>Checkout</Typography>
      <Box sx={{ maxWidth: 500 }}>
        <TextField label="Name" fullWidth value={name} onChange={(e) => setName(e.target.value)} sx={{ mb: 2 }} />
        <TextField label="Address" fullWidth multiline rows={4} value={address} onChange={(e) => setAddress(e.target.value)} sx={{ mb: 2 }} />
        <Typography variant="h6">Total: â‚¹{total}</Typography>
        <Button variant="contained" color="primary" sx={{ mt: 2 }} onClick={submit}>Place Order</Button>
      </Box>
    </Box>
  );
}
