import React from "react";
import ProductCard from "../components/ProductCard";
import "./Product.css";

function Products() {
  const featuredProducts = [
    { id: 1, image: "/images/chocolate.jpg", name: "Chocolate Cake", price: 450 },
    { id: 2, image: "/images/strawberry.jpg", name: "Strawberry Cake", price: 480 },
    { id: 3, image: "/images/vanilla.jpg", name: "Vanilla Cake", price: 400 },
    { id: 4, image: "/images/blackforest.jpg", name: "Black Forest Cake", price: 500 },
  ];

  return (
    <div className="products-section">
      <h2 className="products-title">Featured Cakes</h2>
      <div className="products-grid">
        {featuredProducts.map((p) => (
          <ProductCard
            key={p.id}
            id={p.id}
            image={p.image}
            name={p.name}
            price={p.price}
          />
        ))}
      </div>
    </div>
  );
}

export default Products;
