import React, { useEffect, useState } from "react";
import { Box, Typography, Grid, Card, CardActionArea, CardMedia, CardContent } from "@mui/material";
import { useNavigate } from "react-router-dom";
import { getAllProducts } from "../api/products";

const Categories = () => {
  const [categories, setCategories] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    getAllCategories().then(res => setCategories(res.data)).catch(err => console.error(err));
  }, []);

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" gutterBottom>Shop by Categories</Typography>
      <Grid container spacing={2}>
        {categories.map(cat => (
          <Grid item xs={12} sm={6} md={3} key={cat.id}>
            <Card>
              <CardActionArea onClick={() => navigate(`/category/${cat.id}`)}>
                <CardMedia
                  component="img"
                  height="150"
                  image={cat.image}
                  alt={cat.title}
                />
                <CardContent>
                  <Typography variant="h6">{cat.title}</Typography>
                </CardContent>
              </CardActionArea>
            </Card>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
};

export default Categories;
