// src/components/ToppingsSelector.js
import React from "react";
import {
  FormControl,
  FormLabel,
  FormGroup,
  FormControlLabel,
  Checkbox
} from "@mui/material";

export default function ToppingsSelector({
  title,
  options = [],
  selected = [],
  onChange
}) {
  
  const toggleSelection = (id) => {
    if (selected.includes(id.toString())) {
      onChange(selected.filter((t) => t !== id.toString()));
    } else {
      onChange([...selected, id.toString()]);
    }
  };

  const formatLabel = (opt) => {
    const text = opt.value || opt.name;
    const price = opt.extraPrice ?? opt.extra_price ?? 0;
    return price > 0 ? `${text} (+₹${price})` : text;
  };

  return (
    <FormControl component="fieldset" sx={{ mt: 3 }}>
      <FormLabel component="legend" sx={{ mb: 1, fontWeight: 600 }}>
        {title}
      </FormLabel>

      <FormGroup>
        {options.map((opt) => (
          <FormControlLabel
            key={opt.id}
            control={
              <Checkbox
                checked={selected.includes(opt.id.toString())}
                onChange={() => toggleSelection(opt.id)}
              />
            }
            label={formatLabel(opt)}
          />
        ))}
      </FormGroup>
    </FormControl>
  );
}
