import React from "react";
import { Box, FormControl, FormLabel, FormGroup, FormControlLabel, Checkbox } from "@mui/material";

export default function ToppingsSelector({ options = [], selected = [], onChange }) {
  const toggle = (value) => {
    if (selected.includes(value)) onChange(selected.filter(s => s !== value));
    else onChange([...selected, value]);
  };

  return (
    <Box sx={{ mb: 2 }}>
      <FormControl component="fieldset">
        <FormLabel component="legend">Toppings</FormLabel>
        <FormGroup>
          {options.map(opt => (
            <FormControlLabel
              key={opt.id || opt.value || opt.name}
              control={<Checkbox checked={selected.includes(opt.value ?? opt.name)} onChange={() => toggle(opt.value ?? opt.name)} />}
              label={`${opt.value ?? opt.name} ${opt.extraPrice ? `(₹${opt.extraPrice})` : ""}`}
            />
          ))}
        </FormGroup>
      </FormControl>
    </Box>
  );
}
