import React from "react";
import {
  FormControl,
  FormLabel,
  RadioGroup,
  FormControlLabel,
  Radio
} from "@mui/material";

export default function SingleOptionSelector({ title, options = [], selected, onChange }) {

  const normalizeLabel = (opt) => {
    const displayName = opt.value || opt.name;
    const price = opt.extraPrice ?? opt.extra_price ?? 0;
    return price ? `${displayName} (+₹${price})` : displayName;
  };

  return (
    <FormControl component="fieldset" sx={{ mt: 3 }}>
      <FormLabel component="legend" sx={{ mb: 1, fontWeight: 600 }}>
        {title}
      </FormLabel>

      <RadioGroup
        value={selected?.toString() ?? ""}
        onChange={(e) => onChange(Number(e.target.value))} // send number back
      >
        {options.map((opt) => (
          <FormControlLabel
            key={opt.id}
            value={opt.id.toString()}
            control={<Radio />}
            label={normalizeLabel(opt)}
          />
        ))}
      </RadioGroup>
    </FormControl>
  );
}
