import React, { useState } from "react";
import { Box, Typography, TextField, Button , MenuItem} from "@mui/material";
import { useNavigate, Link } from "react-router-dom";


export function Register({ onRegister }) {
const [username, setUsername] = useState("");
const [password, setPassword] = useState("");
const [role, setRole] = useState("user");
const navigate = useNavigate();


const handleSubmit = () => {
if (!username || !password || !role) return alert("All fields are required");


const users = JSON.parse(localStorage.getItem("users") || "[]");
if (users.find(u => u.username === username)) return alert("Username already exists");


const newUser = { username, password, role };
users.push(newUser);
localStorage.setItem("users", JSON.stringify(users));


onRegister && onRegister(newUser);


alert("Registration successful! Please login.");
navigate("/login");
};


return (
<Box sx={{ maxWidth: 400, mx: "auto", mt: 5 }}>
<Typography variant="h4" sx={{ mb: 3 }}>Register</Typography>


<TextField label="Username" fullWidth value={username} onChange={(e) => setUsername(e.target.value)} sx={{ mb: 2 }} />
<TextField label="Password" type="password" fullWidth value={password} onChange={(e) => setPassword(e.target.value)} sx={{ mb: 2 }} />


<TextField select label="Select Role" fullWidth value={role} onChange={(e) => setRole(e.target.value)} sx={{ mb: 2 }}>
<MenuItem value="user">User</MenuItem>
<MenuItem value="admin">Admin</MenuItem>
</TextField>


<Button variant="contained" color="primary" fullWidth onClick={handleSubmit}>Register</Button>


<Typography sx={{ mt: 2, textAlign: "center" }}>
Already have an account? <Link to="/login">Login here</Link>
</Typography>
</Box>
);
}
