import React, { useEffect, useState } from "react";
import { Box, Typography, TextField, Button, MenuItem } from "@mui/material";
import { getCart } from "../api/cart";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function Checkout() {
  const [cart, setCart] = useState([]);
  const [userName, setUserName] = useState("mahek"); // can be dynamic
  const [deliveryDate, setDeliveryDate] = useState("");
  const [deliverySlot, setDeliverySlot] = useState("10AM-12PM");
  const navigate = useNavigate();

  useEffect(() => {
    getCart()
      .then(res => setCart(res.data))
      .catch(console.error);
  }, []);

  const total = cart.reduce((s, it) => s + (it.totalPrice ?? (it.basePrice * it.quantity)), 0);

  const submit = async () => {
    if (!deliveryDate) return alert("Please select a delivery date");

    try {
      const orderData = { userName, deliveryDate, deliverySlot };
      const res = await axios.post("http://localhost:8080/api/orders/place", orderData);
      alert(res.data);
      navigate("/order-summary"); // redirect to summary page
    } catch (err) {
      console.error(err);
      alert("Failed to place order");
    }
  };

  if (!cart.length) return <Typography sx={{ p: 3 }}>Your cart is empty.</Typography>;

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" sx={{ mb: 2 }}>Checkout</Typography>

      <Box sx={{ maxWidth: 500 }}>
        <TextField
          label="Delivery Date"
          type="date"
          fullWidth
          value={deliveryDate}
          onChange={(e) => setDeliveryDate(e.target.value)}
          sx={{ mb: 2 }}
          InputLabelProps={{ shrink: true }}
        />

        <TextField
          select
          label="Delivery Slot"
          fullWidth
          value={deliverySlot}
          onChange={(e) => setDeliverySlot(e.target.value)}
          sx={{ mb: 2 }}
        >
          <MenuItem value="10AM-12PM">10AM-12PM</MenuItem>
          <MenuItem value="12PM-2PM">12PM-2PM</MenuItem>
          <MenuItem value="2PM-4PM">2PM-4PM</MenuItem>
          <MenuItem value="4PM-6PM">4PM-6PM</MenuItem>
        </TextField>

        <Typography variant="h6">Total: â‚¹{total}</Typography>

        <Button
          variant="contained"
          color="primary"
          sx={{ mt: 2 }}
          onClick={submit}
        >
          Place Order
        </Button>
      </Box>
    </Box>
  );
}
