// src/pages/CartPage.js
import React, { useEffect, useState } from "react";
import { Box, Typography, Paper, Button, Grid } from "@mui/material";
import { getCart } from "../api/cart";
import { useNavigate } from "react-router-dom";

export default function CartPage() {
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    getCart()
      .then((res) => setCart(res.data))
      .catch(console.error);
  }, []);

  const total = cart.reduce(
    (sum, it) => sum + (it.totalPrice ?? it.basePrice * (it.quantity || 1)),
    0
  );

  const handlePlaceOrder = () => {
    navigate("/checkout");
  };

  if (!cart.length)
    return <Typography sx={{ p: 3 }}>Your cart is empty.</Typography>;

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" sx={{ mb: 3 }}>
        My Cart
      </Typography>

      <Grid container spacing={2}>
        {cart.map((item) => {
          // SAFE NORMALIZATION
          const toppings = Array.isArray(item.selectedToppings)
            ? item.selectedToppings
            : item.selectedToppings
            ? [item.selectedToppings]
            : [];

          const shape = item.selectedShape || null;
          const message = item.selectedMessage || "";

          return (
            <Grid item xs={12} key={item.id}>
              <Paper sx={{ p: 2 }}>
                
                {/* PRODUCT NAME */}
                <Typography variant="h6">
                  {item.product?.name || item.name}
                </Typography>

                {/* PRODUCT IMAGE */}
                <img
                  src={`/images/${item.image || item.product?.image}`}
                  alt={item.product?.name}
                  style={{
                    width: 100,
                    height: 100,
                    borderRadius: 8,
                    objectFit: "cover",
                    marginTop: 10,
                  }}
                />

                {/* CUSTOMIZATION DETAILS */}
                {shape && (
                  <Typography sx={{ mt: 1 }}>
                    <strong>Shape:</strong> {shape}
                  </Typography>
                )}

                {toppings.length > 0 && (
                  <Typography sx={{ mt: 1 }}>
                    <strong>Toppings:</strong> {toppings.join(", ")}
                  </Typography>
                )}

                {message && (
                  <Typography sx={{ mt: 1 }}>
                    <strong>Message:</strong> {message}
                  </Typography>
                )}

                {/* QUANTITY */}
                <Typography sx={{ mt: 1 }}>
                  Quantity: {item.quantity}
                </Typography>

                {/* PRICE */}
                <Typography sx={{ mt: 1 }}>
                  Price: ₹{item.totalPrice ?? item.basePrice * item.quantity}
                </Typography>
              </Paper>
            </Grid>
          );
        })}
      </Grid>

      <Typography variant="h6" sx={{ mt: 2 }}>
        Total: ₹{total}
      </Typography>

      <Button
        variant="contained"
        color="primary"
        sx={{ mt: 3 }}
        onClick={handlePlaceOrder}
      >
        Place Order
      </Button>
    </Box>
  );
}
