import React, { useEffect, useState, useCallback } from "react";
import { Box, Typography, Paper, Button, TextField, Grid, Stack } from "@mui/material";
import { getCart, removeFromCart, updateCartItem } from "../api/cart";
import { useNavigate } from "react-router-dom";

// Option mappings
const OPTION_LABELS = {
size: { 4: "Small", 5: "Medium", 6: "Large" },
flavor: { 1: "Chocolate", 2: "Vanilla", 3: "Strawberry" },
shape: { 7: "Round", 8: "Heart", 9: "Square" },
toppings: { 10: "Choco Chips", 11: "Rainbow Sprinkler", 12: "Nuts", 13: "Fruits", 14: "Cherries" },
};

// Safe JSON parse
const safeParse = (val) => {
if (!val) return null;
try { return JSON.parse(val); } catch { return null; }
};

// Helper for image path
const productImagePath = (it) => `/images/${it.image}`;

export default function CartPage({ currentUser }) {
const [items, setItems] = useState([]);
const navigate = useNavigate();

// loadCart wrapped in useCallback to avoid ESLint warning
const loadCart = useCallback(async () => {
if (!currentUser?.username) return;
try {
const res = await getCart(currentUser.username);
setItems(res.data || []);
} catch (err) {
console.error("Failed to load cart:", err);
}
}, [currentUser?.username]);

useEffect(() => {
if (!currentUser?.username) {
alert("Please login or register first to view your cart!");
navigate("/login");
return;
}
loadCart();
}, [currentUser, loadCart, navigate]);

const removeItem = async (id) => {
Â  try {
Â  Â  await removeFromCart(id, currentUser.username);
Â  Â  loadCart();
Â  } catch (err) {
Â  Â  console.error("Failed to remove item:", err);
Â  }
};

const updateQty = async (id, qty) => {
if (qty < 1) return;
try {
await updateCartItem(id, { quantity: qty });
loadCart();
} catch (err) {
console.error("Failed to update quantity:", err);
}
};

const totalPrice = items.reduce((sum, it) => sum + (it.totalPrice ?? it.basePrice * it.quantity), 0);

if (!items.length) return (
    <Box sx={{ p: 4, textAlign: 'center' }}>
        <Typography variant="h5" color="text.secondary">
            Your cart is empty. Start adding some delicious items! ðŸ˜‹
        </Typography>
        <Button variant="contained" sx={{ mt: 3, bgcolor: '#A00045' }} onClick={() => navigate("/")}>
            Start Shopping
        </Button>
    </Box>
);

return ( 
    <Box sx={{ p: { xs: 2, md: 4 }, maxWidth: 1000, mx: 'auto', bgcolor: '#fbfbfb' }}>
        <Typography 
            variant="h4" 
            sx={{ 
                mb: 4, 
                fontWeight: '800', 
                color: '#333', 
                borderBottom: '3px solid #A00045', 
                pb: 1 
            }}
        >
            Your Shopping Cart ðŸ›’
        </Typography> 
        
        <Box display="grid" gap={3}>
            {items.map((it) => {
                const selected = safeParse(it.selectedSingle) || {};
                const toppings = safeParse(it.selectedToppings) || [];
                return (
                    <Paper 
                        key={it.id} 
                        elevation={2} 
                        sx={{ 
                            display: "flex", 
                            p: 3, 
                            borderRadius: 2, 
                            alignItems: "center", 
                            transition: 'box-shadow 0.3s',
                            '&:hover': { elevation: 6, boxShadow: '0 4px 15px rgba(0, 0, 0, 0.1)' }
                        }}
                    >
                        
                        {/* 1. Image and Title (Left Side) */}
                        <Grid container spacing={3} alignItems="center">
                            
                            <Grid item xs={12} sm={3} md={2}>
                                <img
                                    src={productImagePath(it)}
                                    alt={it.name}
                                    style={{ 
                                        width: '100%', 
                                        height: 'auto',
                                        maxHeight: 100,
                                        borderRadius: 8, 
                                        objectFit: "cover",
                                        border: '1px solid #ddd'
                                    }}
                                />
                            </Grid>
                            
                            <Grid item xs={12} sm={9} md={6}>
                                <Stack spacing={0.5}>
                                    <Typography variant="h6" sx={{ fontWeight: 'bold', color: '#A00045' }}>
                                        {it.name}
                                    </Typography>
                                    <Typography variant="body2" color="text.secondary">
                                        <Box component="span" sx={{ fontWeight: 'bold' }}>Size:</Box> {OPTION_LABELS.size[selected.size] ?? "None"}
                                    </Typography> 
                                    <Typography variant="body2" color="text.secondary">
                                        <Box component="span" sx={{ fontWeight: 'bold' }}>Flavor:</Box> {OPTION_LABELS.flavor[selected.flavor] ?? "None"}
                                    </Typography> 
                                    <Typography variant="body2" color="text.secondary">
                                        <Box component="span" sx={{ fontWeight: 'bold' }}>Toppings:</Box> {toppings.length ? toppings.map(id => OPTION_LABELS.toppings[id]).join(", ") : "None"} 
                                    </Typography>
                                    {/* Custom Message Display */}
                                    {it.customMessage && (
                                        <Typography variant="caption" sx={{ color: 'text.primary', fontStyle: 'italic', bgcolor: '#fef7fb', p: 0.5, borderRadius: 1 }}>
                                            **Message:** {it.customMessage}
                                        </Typography>
                                    )}
                                </Stack>
                            </Grid>
                            
                            {/* 2. Quantity, Price, and Remove (Right Side) */}
                            <Grid item xs={12} md={4} sx={{ textAlign: { xs: 'left', md: 'right' } }}>
                                <Box sx={{ 
                                    display: 'flex', 
                                    flexDirection: { xs: 'row', md: 'column' }, 
                                    justifyContent: { xs: 'space-between', md: 'flex-start' }, 
                                    alignItems: { xs: 'center', md: 'flex-end' } 
                                }}>
                                    <TextField
                                        type="number"
                                        label="Qty"
                                        value={it.quantity}
                                        onChange={(e) => updateQty(it.id, Math.max(1, Number(e.target.value)))}
                                        sx={{ width: 80, mb: { xs: 0, md: 1 } }}
                                        InputProps={{ inputProps: { min: 1 } }}
                                        size="small"
                                    /> 
                                    <Typography 
                                        variant="h5" 
                                        sx={{ 
                                            fontWeight: 'bold', 
                                            color: '#000', 
                                            my: 1 
                                        }}
                                    >
                                        â‚¹{it.totalPrice ?? it.basePrice * it.quantity}
                                    </Typography>
                                    <Button 
                                        color="error" 
                                        size="small"
                                        onClick={() => removeItem(it.id)}
                                    >
                                        Remove Item
                                    </Button> 
                                </Box>
                            </Grid>
                        </Grid>
                    </Paper>
                );
            })} 
        </Box>
        
        {/* Cart Summary and Checkout */}
        <Paper elevation={4} sx={{ mt: 4, p: 3, bgcolor: '#ffffff', borderRadius: 2, border: '2px solid #A00045' }}>
            <Box sx={{ display: 'flex', justifyContent: 'flex-end', alignItems: 'baseline', mb: 2 }}>
                <Typography 
                    variant="h5" 
                    sx={{ fontWeight: 'bold', color: '#333' }}
                >
                    Order Total: 
                </Typography>
                <Typography 
                    variant="h4" 
                    sx={{ fontWeight: '900', color: '#A00045', ml: 2 }}
                >
                    â‚¹{totalPrice}
                </Typography>
            </Box>
            <Button 
                variant="contained" 
                color="primary" 
                fullWidth
                sx={{ 
                    py: 1.5, 
                    fontSize: '1.2rem', 
                    fontWeight: 'bold',
                    bgcolor: '#A00045',
                    '&:hover': { bgcolor: '#800037' }
                }} 
                onClick={() => navigate("/checkout")}
            >
                Proceed to Checkout
            </Button> 
        </Paper>
    </Box>
);
}
