import axios from "axios";
const BASE_URL = "http://localhost:8080/api/cart";
export const getCart = () => axios.get(BASE_URL);
export const addToCart = (item) => axios.post(BASE_URL, item);
export const removeFromCart = (id) => axios.delete(`${BASE_URL}/${id}`);
export const updateCartItem = (id, data) => axios.put(`${BASE_URL}/${id}`, data);
