import React, { useState } from "react";
import { Box, Typography, TextField, Button } from "@mui/material";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";

export function Login({ onLogin }) {
const [username, setUsername] = useState("");
const [password, setPassword] = useState("");
const navigate = useNavigate();

const handleSubmit = async () => {
if (!username || !password) {
alert("Please enter username and password");
return;
}


try {
  const res = await axios.post("http://localhost:8080/api/user/login", {
    username,
    password,
  });

  // Check if backend returned a string (error) or user object
  if (typeof res.data === "string") {
    alert(res.data);
    return;
  }

  const user = res.data; // { id, username, role }

  // Save to localStorage and call onLogin
  localStorage.setItem("currentUser", JSON.stringify(user));
  onLogin(user);

  // Navigate based on role
  if (user.role.toUpperCase() === "ADMIN") navigate("/admin");
  else navigate("/"); // normal user

} catch (err) {
  console.error(err);
  alert("Login failed. Check console for details.");
}


};

return (
<Box sx={{ maxWidth: 400, mx: "auto", mt: 5 }}>
<Typography variant="h4" sx={{ mb: 3 }}>Login</Typography>

```
  <TextField
    label="Username"
    fullWidth
    value={username}
    onChange={(e) => setUsername(e.target.value)}
    sx={{ mb: 2 }}
  />

  <TextField
    label="Password"
    type="password"
    fullWidth
    value={password}
    onChange={(e) => setPassword(e.target.value)}
    sx={{ mb: 2 }}
  />

  <Button variant="contained" fullWidth onClick={handleSubmit}>
    Login
  </Button>

  <Typography sx={{ mt: 2 }}>
    Don't have an account? <Link to="/register">Register here</Link>
  </Typography>
</Box>


);
}
