// src/pages/OrdersPage.js
import React, { useEffect, useState } from "react";
import { Typography, Box, Paper, Grid } from "@mui/material";
import { getOrdersByUser } from "../api/orders";
import { productImagePath } from "../utils/imageHelpers"; // helper to get image path

export default function OrdersPage() {
  const [orders, setOrders] = useState([]);
  const [name, setName] = useState(""); // user-entered name

  useEffect(() => {
    const username = prompt("Enter your name to view orders") || "";
    setName(username);

    if (username) {
      getOrdersByUser(username)
        .then((res) => setOrders(res.data))
        .catch(console.error);
    }
  }, []);

  if (!orders.length)
    return <Typography sx={{ p: 3 }}>No orders found for {name}</Typography>;

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" sx={{ mb: 3 }}>
        My Orders
      </Typography>

      <Grid container spacing={2}>
        {orders.map((order) => (
          <Grid item xs={12} key={order.id}>
            <Paper sx={{ p: 2 }}>
              <Typography>Order ID: {order.id}</Typography>
              <Typography>Name: {order.userName}</Typography>
              <Typography>
                Delivery: {new Date(order.deliveryDate).toLocaleDateString()}
              </Typography>
              <Typography>Status: {order.status}</Typography>
              <Typography>Total: ₹{order.totalPrice}</Typography>
              <Typography sx={{ mt: 1, mb: 1 }}>Items:</Typography>

              {order.items.map((it, idx) => (
                <Paper
                  key={idx}
                  sx={{
                    display: "flex",
                    alignItems: "center",
                    gap: 2,
                    p: 1,
                    mb: 1,
                  }}
                >
                  <img
                    src={
                      it.image
                        ? it.image
                        : productImagePath({ id: it.productId }) // fallback
                    }
                    alt={it.name}
                    style={{ width: 80, height: 80, objectFit: "cover", borderRadius: 8 }}
                  />
                  <Box>
                    <Typography>{it.name}</Typography>
                    <Typography>Quantity: {it.quantity}</Typography>
                    <Typography>Price: ₹{it.totalPrice}</Typography>
                  </Box>
                </Paper>
              ))}
            </Paper>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}
