// src/pages/Home.js
import React, { useEffect, useState } from "react";
import {
  Box,
  Typography,
  Grid,
  CircularProgress,
  Paper,
  Divider,
  Fade,
} from "@mui/material";
import { useNavigate } from "react-router-dom";
import ProductCard from "../components/ProductCard";
import { getAllProducts } from "../api/products";
import Categories from "../components/Categories";

const Home = () => {
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const navigate = useNavigate();

  useEffect(() => {
    getAllProducts()
      .then((res) => setProducts(res.data))
      .catch((err) => console.error(err))
      .finally(() => setLoading(false));
  }, []);

  const handleCustomize = (productId) => {
    navigate(`/customize/${productId}`);
  };

  if (loading)
    return (
      <Box sx={{ textAlign: "center", mt: 10 }}>
        <CircularProgress sx={{ color: "#ffb6c1" }} />
      </Box>
    );

  return (
    <Fade in timeout={600}>
      <Box
        sx={{
          p: 4,
          background: "#fdeef4", // pastel pink background
          minHeight: "100vh",
        }}
      >
        {/* ---- Hero Section ---- */}
        <Paper
          elevation={0}
          sx={{
            p: 6,
            mb: 5,
            borderRadius: 4,
            background:
              "linear-gradient(135deg, #ffd6e8 0%, #ffeaf3 100%)",
            textAlign: "center",
          }}
        >
          <Typography variant="h3" fontWeight="bold" sx={{ color: "#d63384" }}>
            Welcome to SweetBites üç∞
          </Typography>
          <Typography variant="h6" sx={{ mt: 1, color: "#885066" }}>
            Customize & order cakes in your favorite pastel world üéÄ
          </Typography>
        </Paper>

        {/* ---- Categories Section ---- */}
        <Box sx={{ mb: 5 }}>
          <Typography
            variant="h4"
            sx={{ mb: 2, fontWeight: "bold", color: "#c2185b" }}
          >
            Browse Categories
          </Typography>

          <Paper
            elevation={3}
            sx={{
              p: 3,
              borderRadius: 3,
              backgroundColor: "#fff0f6",
              border: "1px solid #ffd1df",
            }}
          >
            <Categories />
          </Paper>
        </Box>

        <Divider sx={{ my: 4, borderColor: "#ffb6c1" }} />

        {/* ---- Product Section ---- */}
        <Typography
          variant="h4"
          sx={{
            mb: 3,
            fontWeight: "bold",
            color: "#c2185b",
          }}
        >
          All Products
        </Typography>

        <Grid container spacing={4}>
          {products.map((product) => (
            <Grid item xs={12} sm={6} md={3} key={product.id}>
              <Paper
                elevation={4}
                sx={{
                  p: 1,
                  borderRadius: 3,
                  backgroundColor: "#fff5fa",
                  border: "1px solid #ffe0eb",
                  transition: "0.3s ease",
                  "&:hover": {
                    transform: "translateY(-6px)",
                    boxShadow: "0 6px 20px rgba(255, 182, 193, 0.4)",
                  },
                }}
              >
                <ProductCard
                  product={product}
                  onCustomize={handleCustomize}
                />
              </Paper>
            </Grid>
          ))}
        </Grid>
      </Box>
    </Fade>
  );
};

export default Home;
