// src/pages/OrdersPage.js
import React, { useEffect, useState } from "react";
import { Box, Typography, Grid, Paper, CircularProgress } from "@mui/material";
import { getOrdersByUser } from "../api/orders";
import { productImagePath } from "../utils/imageHelpers";

export default function OrdersPage() {
  const [orders, setOrders] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const username = localStorage.getItem("username");
    if (!username) {
      setOrders([]);
      setLoading(false);
      return;
    }

    getOrdersByUser(username)
      .then((res) => setOrders(res.data))
      .catch(console.error)
      .finally(() => setLoading(false));
  }, []);

  if (loading)
    return <CircularProgress sx={{ mt: 5, display: "block", mx: "auto" }} />;

  if (!orders.length)
    return <Typography sx={{ p: 3 }}>You have not placed any orders yet.</Typography>;

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" sx={{ mb: 3 }}>My Orders</Typography>
      <Grid container spacing={2}>
        {orders.map((order) => (
          <Grid item xs={12} key={order.id}>
            <Paper sx={{ p: 2 }}>
              <Typography variant="h6">Order ID: {order.id}</Typography>
              <Typography>User: {order.userName}</Typography>
              <Typography>Total: ₹{order.totalPrice}</Typography>
              <Typography>
                Delivery Date: {new Date(order.deliveryDate).toLocaleDateString()}
              </Typography>
              <Typography>Delivery Slot: {order.deliverySlot}</Typography>

              <Box sx={{ mt: 2 }}>
                <Typography variant="subtitle1">Items:</Typography>
                <Grid container spacing={2}>
                  {order.items.map((item, idx) => (
                    <Grid item xs={12} sm={6} md={4} key={idx}>
                      <Paper sx={{ p: 2, display: "flex", flexDirection: "column", alignItems: "center" }}>
                        <img
                          src={productImagePath(item.product)}
                          alt={item.product?.name || item.name}
                          style={{ width: 120, height: 120, objectFit: "cover", borderRadius: 8, marginBottom: 8 }}
                        />
                        <Typography variant="subtitle1">
                          {item.product?.name || item.name}
                        </Typography>
                        <Typography>Quantity: {item.quantity}</Typography>
                        <Typography>Price: ₹{item.totalPrice}</Typography>
                        {item.selectedSingle && Object.keys(item.selectedSingle).length > 0 && (
                          <Typography>
                            Options: {JSON.stringify(item.selectedSingle)}
                          </Typography>
                        )}
                        {item.selectedToppings && item.selectedToppings.length > 0 && (
                          <Typography>
                            Toppings: {item.selectedToppings.join(", ")}
                          </Typography>
                        )}
                      </Paper>
                    </Grid>
                  ))}
                </Grid>
              </Box>
            </Paper>
          </Grid>
        ))}
      </Grid>
    </Box>
  );
}
