// src/components/ToppingsSelector.js
import React from "react";
import { Box, FormControl, FormLabel, FormGroup, FormControlLabel, Checkbox } from "@mui/material";

const ToppingsSelector = ({ options, selectedToppings, onChange }) => {

  const handleToggle = (topping) => {
    if (selectedToppings.includes(topping)) {
      onChange(selectedToppings.filter(t => t !== topping));
    } else {
      onChange([...selectedToppings, topping]);
    }
  };

  return (
    <Box sx={{ mb: 3 }}>
      <FormControl component="fieldset">
        <FormLabel component="legend">Toppings</FormLabel>
        <FormGroup>
          {options.map(opt => (
            <FormControlLabel
              key={opt.name}
              control={
                <Checkbox
                  checked={selectedToppings.includes(opt.name)}
                  onChange={() => handleToggle(opt.name)}
                />
              }
              label={`${opt.name} (+₹${opt.price})`}
            />
          ))}
        </FormGroup>
      </FormControl>
    </Box>
  );
};

export default ToppingsSelector;
