import React, { useState } from "react";
import { Box, Typography, TextField, Button, Snackbar, Alert } from "@mui/material";
import { useNavigate, Link } from "react-router-dom";
import axios from "axios";

export function Login({ onLogin }) {
const [username, setUsername] = useState("");
const [password, setPassword] = useState("");
const [snackbar, setSnackbar] = useState({ open: false, message: "", severity: "success" });
const navigate = useNavigate();

const handleSubmit = async (e) => {
e.preventDefault(); // prevent default form submit
if (!username || !password) {
setSnackbar({ open: true, message: "Please enter username and password", severity: "error" });
return;
}


try {
  const res = await axios.post("http://localhost:8080/api/user/login", { username, password });

  if (typeof res.data === "string") {
    setSnackbar({ open: true, message: res.data, severity: "error" });
    return;
  }

  const user = res.data;
  localStorage.setItem("currentUser", JSON.stringify(user));
  onLogin(user);

  setSnackbar({ open: true, message: "Logged in successfully!", severity: "success" });

  // Navigate after small delay to show Snackbar
  setTimeout(() => {
    if (user.role.toUpperCase() === "ADMIN") navigate("/admin");
    else navigate("/"); // normal user
  }, 500);

} catch (err) {
  console.error(err);
  setSnackbar({ open: true, message: "Login failed. Check console for details.", severity: "error" });
}


};

return (
<Box sx={{ maxWidth: 400, mx: "auto", mt: 5 }}>
<Typography variant="h4" sx={{ mb: 3 }}>Login</Typography>


  <form onSubmit={handleSubmit}>
    <TextField
      label="Username"
      fullWidth
      value={username}
      onChange={(e) => setUsername(e.target.value)}
      sx={{ mb: 2 }}
    />

    <TextField
      label="Password"
      type="password"
      fullWidth
      value={password}
      onChange={(e) => setPassword(e.target.value)}
      sx={{ mb: 2 }}
    />

    <Button type="submit" variant="contained" fullWidth sx={{ mb: 2 }}>
      Login
    </Button>
  </form>

  <Typography>
    Don't have an account? <Link to="/register">Register here</Link>
  </Typography>

  <Snackbar
    open={snackbar.open}
    autoHideDuration={3000}
    onClose={() => setSnackbar({ ...snackbar, open: false })}
    anchorOrigin={{ vertical: "top", horizontal: "center" }}
  >
    <Alert severity={snackbar.severity} sx={{ width: "100%" }}>
      {snackbar.message}
    </Alert>
  </Snackbar>
</Box>


);
}
