// src/pages/Login.js
import React, { useState } from "react";
import { Box, Typography, TextField, Button } from "@mui/material";
import { useNavigate, Link } from "react-router-dom";

export default function Login({ onLogin }) {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleSubmit = () => {
    if (!username || !password) return alert("Please enter username and password");

    // Simple check against stored users
    const users = JSON.parse(localStorage.getItem("users") || "[]");
    const user = users.find(u => u.username === username && u.password === password);
    if (!user) return alert("Invalid credentials");

    onLogin(user);
    navigate("/");
  };

  return (
    <Box sx={{ maxWidth: 400, mx: "auto", mt: 5 }}>
      <Typography variant="h4" sx={{ mb: 3 }}>Login</Typography>
      <TextField label="Username" fullWidth value={username} onChange={(e) => setUsername(e.target.value)} sx={{ mb: 2 }} />
      <TextField label="Password" type="password" fullWidth value={password} onChange={(e) => setPassword(e.target.value)} sx={{ mb: 2 }} />
      <Button variant="contained" color="primary" fullWidth onClick={handleSubmit}>Login</Button>
      <Typography sx={{ mt: 2 }}>Don't have an account? <Link to="/register">Register here</Link></Typography>
    </Box>
  );
}
