// src/pages/CartPage.js
import React, { useEffect, useState } from "react";
import { Box, Typography, Button, Paper } from "@mui/material";
import { getCart } from "../api/cart";
import { useNavigate } from "react-router-dom";

export default function CartPage() {
  const [cart, setCart] = useState([]);
  const navigate = useNavigate();

  useEffect(() => {
    getCart()
      .then(res => setCart(res.data))
      .catch(console.error);
  }, []);

  const total = cart.reduce((sum, it) => sum + (it.totalPrice ?? it.basePrice * (it.quantity || 1)), 0);

  const handleCheckout = () => {
    const username = localStorage.getItem("username");
    if (!username) {
      alert("Please login first!");
      navigate("/login");
      return;
    }
    navigate("/checkout");
  };

  if (!cart.length) return <Typography sx={{ p: 3 }}>Your cart is empty.</Typography>;

  return (
    <Box sx={{ p: 3 }}>
      <Typography variant="h4" sx={{ mb: 3 }}>My Cart</Typography>
      {cart.map(item => (
        <Paper key={item.id} sx={{ p: 2, mb: 2 }}>
          <Typography>{item.productName}</Typography>
          <Typography>Qty: {item.quantity}</Typography>
          <Typography>Price: ₹{item.totalPrice ?? item.basePrice * item.quantity}</Typography>
        </Paper>
      ))}
      <Typography variant="h6" sx={{ mt: 2 }}>Total: ₹{total}</Typography>
      <Button variant="contained" sx={{ mt: 2 }} onClick={handleCheckout}>
        Proceed to Checkout
      </Button>
    </Box>
  );
}
