import React, { useEffect, useState } from "react";
import { Box, Typography, TextField, Button, CircularProgress } from "@mui/material";
import { useParams, useNavigate } from "react-router-dom";
import { getProductById } from "../api/products";
import { getOptionsByType } from "../api/options";
import { calculatePrice } from "../api/price";
import { addToCart } from "../api/cart";
import SingleOptionSelector from "../components/SingleOptionSelector";
import ToppingsSelector from "../components/ToppingsSelector";
import PriceBox from "../components/PriceBox";

export default function CustomizerPage() {
  const { productId } = useParams();
  const navigate = useNavigate();

  const [product, setProduct] = useState(null);
  const [options, setOptions] = useState({});
  const [selectedSingle, setSelectedSingle] = useState({});
  const [selectedToppings, setSelectedToppings] = useState([]);
  const [quantity, setQuantity] = useState(1);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(true);

  // Fetch product and options
  useEffect(() => {
    Promise.all([
      getProductById(productId),
      getOptionsByType("topping"),
      getOptionsByType("size"),
      getOptionsByType("flavor"),
      getOptionsByType("shape"),
      getOptionsByType("decoration")
    ])
      .then(([prodRes, toppingRes, sizeRes, flavorRes, shapeRes, decoRes]) => {
        setProduct(prodRes.data);
        setOptions({
          topping: toppingRes.data,
          size: sizeRes.data,
          flavor: flavorRes.data,
          shape: shapeRes.data,
          decoration: decoRes.data
        });
      })
      .catch(console.error)
      .finally(() => setLoading(false));
  }, [productId]);

  // Calculate price whenever selections change
  useEffect(() => {
    if (!product) return;

    calculatePrice({
      basePrice: product.basePrice,
      selectedSingle,
      selectedToppings,
      quantity
    })
      .then(res => setTotal(res.data.total))
      .catch(() => {
        // fallback client-side calculation
        let tmp = product.basePrice;
        Object.values(selectedSingle).forEach(val => {
          const found = [].concat(...Object.values(options)).find(o => (o.value ?? o.name) === val);
          if (found && found.extraPrice) tmp += found.extraPrice;
        });
        selectedToppings.forEach(t => {
          const found = (options.topping || []).find(o => (o.value ?? o.name) === t);
          if (found && found.extraPrice) tmp += found.extraPrice;
        });
        setTotal(tmp * quantity);
      });
  }, [product, selectedSingle, selectedToppings, quantity, options]);

  const handleAddToCart = () => {
    const payload = {
      productId: product.id,
      name: product.name,
      image: product.image,
      basePrice: product.basePrice,
      selectedSingle,
      selectedToppings,
      quantity,
      totalPrice: total
    };
    addToCart(payload)
      .then(() => navigate("/cart"))
      .catch(console.error);
  };

  if (loading) return <Box sx={{ textAlign: "center", mt: 6 }}><CircularProgress /></Box>;
  if (!product) return <Typography sx={{ p: 3 }}>Product not found.</Typography>;

  const isPastry = product.category_id === 7; // skip options for pastries

  return (
    <Box>
      <Typography variant="h4" sx={{ mb: 2 }}>{product.name}</Typography>
      <img src={`/images/${product.image}`} alt={product.name} style={{ width: 300, borderRadius: 8 }} />
      <Typography variant="body1" sx={{ mt: 2 }}>{product.description}</Typography>

      <Box sx={{ mt: 3 }}>
        {/* Only show options if not a pastry */}
        {!isPastry && (
          <>
            <SingleOptionSelector
              title="Size"
              options={options.size || []}
              selected={selectedSingle.size}
              onChange={(v) => setSelectedSingle(prev => ({ ...prev, size: v }))}
            />
            <SingleOptionSelector
              title="Flavor"
              options={options.flavor || []}
              selected={selectedSingle.flavor}
              onChange={(v) => setSelectedSingle(prev => ({ ...prev, flavor: v }))}
            />
            <SingleOptionSelector
              title="Shape"
              options={options.shape || []}
              selected={selectedSingle.shape}
              onChange={(v) => setSelectedSingle(prev => ({ ...prev, shape: v }))}
            />
            <SingleOptionSelector
              title="Decoration"
              options={options.decoration || []}
              selected={selectedSingle.decoration}
              onChange={(v) => setSelectedSingle(prev => ({ ...prev, decoration: v }))}
            />
            <ToppingsSelector
              title="Toppings"
              options={options.topping || []}
              selected={selectedToppings}
              onChange={setSelectedToppings}
            />
          </>
        )}

        <TextField
          type="number"
          label="Quantity"
          value={quantity}
          onChange={(e) => setQuantity(Math.max(1, parseInt(e.target.value || "1")))}
          sx={{ mt: 2, width: 120 }}
          InputProps={{ inputProps: { min: 1 } }}
        />

        <PriceBox price={total} />

        <Button variant="contained" color="primary" sx={{ mt: 2 }} onClick={handleAddToCart}>
          Add to Cart
        </Button>
      </Box>
    </Box>
  );
}
