import React from "react";
import { BrowserRouter, Routes, Route, Navigate } from "react-router-dom";
import Navbar from "./components/Navbar";
import Home from "./pages/Home";
import CategoryPage from "./pages/CategoryPage";
import CategoryProducts from "./pages/CategoryProducts";
import ProductDetails from "./pages/ProductDetails";
import CustomizerPage from "./pages/CustomizerPage";
import CartPage from "./pages/CartPage";
import Checkout from "./pages/Checkout";
import OrdersPage from "./pages/OrdersPage";
import LoginPage from "./pages/LoginPage"; // We'll create this
import { Container } from "@mui/material";

function App() {
  const username = localStorage.getItem("username");

  return (
    <BrowserRouter>
      <Navbar />
      <Container maxWidth="lg" sx={{ mt: 3, mb: 6 }}>
        <Routes>
          <Route path="/" element={<Home />} />
          <Route path="/categories" element={<CategoryProducts />} />
          <Route path="/category/:categoryId" element={<CategoryPage />} />
          <Route path="/product/:productId" element={<ProductDetails />} />
          <Route path="/customize/:productId" element={<CustomizerPage />} />

          {/* Protected routes */}
          <Route
            path="/cart"
            element={username ? <CartPage /> : <Navigate to="/login" />}
          />
          <Route
            path="/checkout"
            element={username ? <Checkout /> : <Navigate to="/login" />}
          />
          <Route
            path="/orders"
            element={username ? <OrdersPage /> : <Navigate to="/login" />}
          />

          {/* Login page */}
          <Route path="/login" element={<LoginPage />} />

          {/* Fallback */}
          <Route path="*" element={<Navigate to="/" />} />
        </Routes>
      </Container>
    </BrowserRouter>
  );
}

export default App;
