// src/pages/AdminDashboard.js
import React, { useEffect, useState } from "react";
import { Box, Typography, Paper, TextField, Button } from "@mui/material";

export default function AdminDashboard() {
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    // Load ALL orders saved in app
    const allOrders = JSON.parse(localStorage.getItem("orders") || "[]");
    setOrders(allOrders);
  }, []);

  const updateStatus = (id, newStatus) => {
    const updated = orders.map((o) =>
      o.id === id ? { ...o, status: newStatus } : o
    );

    setOrders(updated);
    localStorage.setItem("orders", JSON.stringify(updated));
  };

  return (
    <Box sx={{ mt: 4 }}>
      <Typography variant="h4" sx={{ mb: 3 }}>
        Admin Dashboard — All Orders
      </Typography>

      {orders.length === 0 && (
        <Typography>No orders found.</Typography>
      )}

      {orders.map((order) => (
        <Paper key={order.id} sx={{ p: 2, mb: 2 }}>
          <Typography><b>User:</b> {order.username}</Typography>
          <Typography><b>Total:</b> ₹{order.total}</Typography>
          <Typography><b>Status:</b> {order.status}</Typography>

          <TextField
            label="Update Status"
            fullWidth
            defaultValue={order.status}
            sx={{ my: 2 }}
            onBlur={(e) => updateStatus(order.id, e.target.value)}
          />

          <Button
            variant="contained"
            onClick={() => updateStatus(order.id, "Completed")}
          >
            Mark Completed
          </Button>
        </Paper>
      ))}
    </Box>
  );
}
