// src/components/ProductCard.js
import React from "react";
import { Card, CardMedia, CardContent, Typography, Button } from "@mui/material";

const ProductCard = ({ product, onCustomize }) => {
  return (
    <Card>
      <CardMedia
        component="img"
        height="180"
        image={product.image}
        alt={product.name}
      />
      <CardContent>
        <Typography variant="h6">{product.name}</Typography>
        <Typography variant="subtitle1" color="text.secondary">â‚¹{product.basePrice}</Typography>
        <Button
          variant="contained"
          color="secondary"
          sx={{ mt: 1 }}
          onClick={() => onCustomize(product.id)}
        >
          Customize
        </Button>
      </CardContent>
    </Card>
  );
};

export default ProductCard;
