// src/pages/LoginPage.js
import React, { useState } from "react";
import {
  Box,
  TextField,
  Button,
  Typography,
  Paper,
  Link as MuiLink,
} from "@mui/material";
import axios from "axios";
import { useNavigate } from "react-router-dom";

export default function LoginPage() {
  const [username, setUsername] = useState("");
  const [password, setPassword] = useState("");
  const navigate = useNavigate();

  const handleLogin = async () => {
    if (!username || !password) {
      alert("Please fill both fields");
      return;
    }

    try {
      const res = await axios.post("http://localhost:8080/api/user/login", {
        username,
        password,
      });

      if (res.data === "User not found!") {
        alert("User not found! Redirecting to Register...");
        navigate("/register");
        return;
      }

      if (res.data === "Invalid password!") {
        alert("Wrong password!");
        return;
      }

      if (res.data === "Login successful!") {
        // Store full user object in localStorage
        const userObj = { username }; // or use res.data.user if backend sends full object
        localStorage.setItem("user", JSON.stringify(userObj));

        // Redirect user back where they came from (checkout/cart)
        const redirect = localStorage.getItem("redirectAfterLogin");
        if (redirect) {
          localStorage.removeItem("redirectAfterLogin");
          navigate(redirect);
        } else {
          navigate("/");
        }
      }
    } catch (err) {
      console.error(err);
      alert("Something went wrong!");
    }
  };

  return (
    <Box
      sx={{
        maxWidth: 400,
        mx: "auto",
        mt: 5,
      }}
    >
      <Paper sx={{ p: 4, borderRadius: 3 }}>
        <Typography variant="h4" sx={{ mb: 3, textAlign: "center" }}>
          Login
        </Typography>

        <TextField
          label="Username"
          fullWidth
          sx={{ mb: 2 }}
          value={username}
          onChange={(e) => setUsername(e.target.value)}
        />

        <TextField
          label="Password"
          type="password"
          fullWidth
          sx={{ mb: 2 }}
          value={password}
          onChange={(e) => setPassword(e.target.value)}
        />

        <Button
          variant="contained"
          fullWidth
          sx={{ mt: 1 }}
          onClick={handleLogin}
        >
          Login
        </Button>

        <Typography sx={{ mt: 2, textAlign: "center" }}>
          Donâ€™t have an account?{" "}
          <MuiLink
            component="button"
            onClick={() => navigate("/register")}
            underline="hover"
            sx={{ cursor: "pointer" }}
          >
            Register
          </MuiLink>
        </Typography>
      </Paper>
    </Box>
  );
}
