import React, { useEffect, useState, useMemo } from "react";
import {
  Box,
  Typography,
  TextField,
  Button,
  CircularProgress,
} from "@mui/material";
import { useParams, useNavigate } from "react-router-dom";
import { getProductById } from "../api/products";
import { getOptionsByType } from "../api/options";
import { addToCart } from "../api/cart";
import SingleOptionSelector from "../components/SingleOptionSelector";
import ToppingsSelector from "../components/ToppingsSelector";
import PriceBox from "../components/PriceBox";

export default function CustomizerPage({ currentUser }) {
  const { productId } = useParams();
  const navigate = useNavigate();

  const [product, setProduct] = useState(null);
  const [opts, setOpts] = useState({});
  const [selectedSingle, setSelectedSingle] = useState({});
  const [selectedToppings, setSelectedToppings] = useState([]);
  const [customMessage, setCustomMessage] = useState("");

  const [quantity, setQuantity] = useState(1);
  const [total, setTotal] = useState(0);
  const [loading, setLoading] = useState(true);

  // Fetch
  useEffect(() => {
    Promise.all([
      getProductById(productId),
      getOptionsByType("topping"),
      getOptionsByType("size"),
      getOptionsByType("flavor"),
      getOptionsByType("shape"),
      getOptionsByType("decoration"),
    ])
      .then(([pRes, tRes, sizeRes, flavorRes, shapeRes, decoRes]) => {
        setProduct(pRes.data);
        setOpts({
          topping: tRes.data,
          size: sizeRes.data,
          flavor: flavorRes.data,
          shape: shapeRes.data,
          decoration: decoRes.data,
        });
      })
      .catch(console.error)
      .finally(() => setLoading(false));
  }, [productId]);

  const totalPrice = useMemo(() => {
    if (!product) return 0;
    let tmp = product.basePrice;

    Object.entries(selectedSingle).forEach(([key, id]) => {
      const found = (opts[key] || []).find(o => o.id.toString() === id.toString());
      if (found) tmp += found.extraPrice ?? found.extra_price ?? 0;
    });

    selectedToppings.forEach(tid => {
      const found = (opts.topping || []).find(o => o.id.toString() === tid.toString());
      if (found) tmp += found.extraPrice ?? found.extra_price ?? 0;
    });

    return tmp * quantity;
  }, [product, selectedSingle, selectedToppings, quantity, opts]);

  useEffect(() => {
    setTotal(totalPrice);
  }, [totalPrice]);

  const handleAddToCart = () => {
    if (!currentUser?.username) {
      alert("Please login first!");
      navigate("/login");
      return;
    }

    const payload = {
      username: currentUser.username,
      productId: product.id,
      name: product.name,
      image: product.image,
      basePrice: product.basePrice,
      quantity,
      totalPrice: total,
      selectedSingle: JSON.stringify(selectedSingle),
      selectedToppings: JSON.stringify(selectedToppings),
      customMessage: customMessage.trim() || null,
    };

    addToCart(payload)
      .then(() => navigate("/cart"))
      .catch(console.error);
  };

  if (loading)
    return (
      <Box sx={{ textAlign: "center", mt: 6 }}>
        <CircularProgress />
      </Box>
    );

  if (!product)
    return <Typography sx={{ p: 3 }}>Product not found.</Typography>;

  return (
    <Box
      sx={{
        p: 3,
        maxWidth: 800,
        mx: "auto",
        background: "#ffeef5",
        borderRadius: 4,
        boxShadow: "0 0 20px rgba(255, 182, 193, 0.3)",
      }}
    >
      <Typography
        variant="h4"
        sx={{
          mb: 2,
          fontWeight: "bold",
          textAlign: "center",
          color: "#d63384",
        }}
      >
        Customize Your Cake üç∞
      </Typography>

      <img
        src={`/images/${product.image}`}
        alt={product.name}
        style={{
          width: "100%",
          maxHeight: 300,
          objectFit: "cover",
          borderRadius: 12,
          border: "3px solid #ffcce1",
        }}
      />

      <Typography
        variant="body1"
        sx={{
          mt: 2,
          color: "#8b3a62",
          textAlign: "center",
          fontStyle: "italic",
        }}
      >
        {product.description}
      </Typography>

      <Box
        sx={{
          mt: 3,
          p: 3,
          border: "2px solid #ffb6c1",
          borderRadius: 4,
          background: "#fff6fa",
          boxShadow: "0 0 12px rgba(255, 182, 193, 0.25)",
        }}
      >
        <Typography
          variant="h6"
          sx={{
            mb: 2,
            pb: 1,
            borderBottom: "2px dashed #ff9ec9",
            color: "#d63384",
            fontWeight: "bold",
          }}
        >
          Cake Options üéÄ
        </Typography>

        <SingleOptionSelector
          title="Size"
          options={opts.size || []}
          selected={selectedSingle.size}
          onChange={(v) => setSelectedSingle(prev => ({ ...prev, size: v }))}
        />

        <SingleOptionSelector
          title="Flavor"
          options={opts.flavor || []}
          selected={selectedSingle.flavor}
          onChange={(v) => setSelectedSingle(prev => ({ ...prev, flavor: v }))}
        />

        <SingleOptionSelector
          title="Shape"
          options={opts.shape || []}
          selected={selectedSingle.shape}
          onChange={(v) => setSelectedSingle(prev => ({ ...prev, shape: v }))}
        />

        <SingleOptionSelector
          title="Decoration"
          options={opts.decoration || []}
          selected={selectedSingle.decoration}
          onChange={(v) => setSelectedSingle(prev => ({ ...prev, decoration: v }))}
        />

        <ToppingsSelector
          title="Toppings"
          options={opts.topping || []}
          selected={selectedToppings}
          onChange={setSelectedToppings}
        />

        <TextField
          label="Custom Message"
          placeholder="Write something sweet üíï"
          fullWidth
          multiline
          rows={3}
          value={customMessage}
          onChange={(e) => setCustomMessage(e.target.value)}
          sx={{
            mt: 3,
            mb: 2,
            "& .MuiOutlinedInput-root": {
              borderRadius: 3,
              background: "#fff0f6",
            },
          }}
        />

        <Box
          sx={{
            my: 3,
            height: "1px",
            background: "linear-gradient(to right, transparent, #ff9ec9, transparent)",
          }}
        />

        <TextField
          type="number"
          label="Quantity"
          value={quantity}
          onChange={(e) =>
            setQuantity(Math.max(1, parseInt(e.target.value || "1")))
          }
          sx={{
            width: 120,
            "& .MuiOutlinedInput-root": {
              borderRadius: 3,
              background: "#fff0f6",
            },
          }}
          InputProps={{ inputProps: { min: 1 } }}
        />

        <PriceBox price={total} />

        <Button
          variant="contained"
          sx={{
            mt: 3,
            background: "#ff76b8",
            "&:hover": { background: "#ff5ba3" },
            color: "white",
            fontWeight: "bold",
            borderRadius: 3,
            px: 4,
            py: 1.2,
            boxShadow: "0 4px 12px rgba(255, 105, 180, 0.4)",
          }}
          onClick={handleAddToCart}
        >
          Add to Cart üíù
        </Button>
      </Box>
    </Box>
  );
}
