// src/components/SingleOptionSelector.js
import React from "react";
import { Box, FormControl, FormLabel, RadioGroup, FormControlLabel, Radio } from "@mui/material";

const SingleOptionSelector = ({ options, selectedOption, onChange }) => {
  return (
    <Box sx={{ mb: 3 }}>
      <FormControl component="fieldset">
        <FormLabel component="legend">Choose One</FormLabel>
        <RadioGroup
          value={selectedOption || ""}
          onChange={e => onChange(e.target.value)}
        >
          {options.map(opt => (
            <FormControlLabel
              key={opt.name}
              value={opt.name}
              control={<Radio />}
              label={`${opt.name} (+₹${opt.price})`}
            />
          ))}
        </RadioGroup>
      </FormControl>
    </Box>
  );
};

export default SingleOptionSelector;
